create table customer_Service(Customer_service_ID number(10) ,Emp_Name varchar2(10),constraint csid_pk Primary key (Customer_service_ID));create table employees_Emails(Customer_service_ID number(10) ,Email varchar2(10),constraint emails_pk Primary key (Email,Customer_service_ID),constraint emails_fk foreign key (Customer_service_ID) references Customer_Service(Customer_service_ID));create table employees_Phone_numbers(Customer_service_ID number(10) ,Phone_number number(10),constraint Phone_numbers_pk Primary key (Phone_number,Customer_service_ID),constraint Phone_numbers_fk foreign key (Customer_service_ID) references Customer_Service(Customer_service_ID));create table employees_Languages(Customer_service_ID number(10) ,Language varchar2(10),constraint Languages_pk Primary key (Language,Customer_service_ID),constraint Languages_fk foreign key (Customer_service_ID) references Customer_Service(Customer_service_ID));create table Customers(Customer_ID number(10),First_Name varchar2(10),Last_Name varchar2(10), Address varchar2(20),Email varchar2(20),Customer_service_ID number(10) ,constraint cusid_pk Primary key (Customer_ID),constraint customerservice_fk foreign key (Customer_service_ID) references Customer_Service(Customer_service_ID));create table Customers_phone_numbers(Customer_ID number(10),Phone_number number(10),constraint customersnumbers_pk Primary key (Customer_ID,Phone_number),constraint customersnumbers_fk foreign key (Customer_ID) references Customers(Customer_ID));create table Orders(Order_ID number(10),Order_Status varchar2(10),Order_Date varchar2(10),Customer_ID number(10),constraint ordersid_pk Primary key (Order_ID),constraint customersnumbers_fk foreign key (Customer_ID) references Customers(Customer_ID));create table Orders_items(Order_ID number(10),Item varchar2(10),Price number(10),constraint orderitems_pk Primary key (Order_ID,Item,Price),constraint orderitems_fk foreign key (Order_ID) references Orders(Order_ID));create table Pharmacy(Pharmacy_ID number(10),Name varchar2(10),Hotline number(10),constraint pharid_pk Primary key (Pharmacy_ID));create table Pharmacy_Addresses(Pharmacy_ID number(10),Address varchar2(10),constraint address_pk Primary key (Pharmacy_ID,Address),constraint pharid_fk foreign key (Pharmacy_ID) references Pharmacy(Pharmacy_ID));create table Drugs(Pharmacy_ID number(10),Drug_Trade_Name varchar2(20),Sell_Price number(10),constraint drugs_pk Primary key (Pharmacy_ID,Drug_Trade_Name),constraint pharid_fk foreign key (Pharmacy_ID) references Pharmacy(Pharmacy_ID));create table Restaurant(Restaurant_ID number(10),Name varchar2(20),Hotline number(10),Stars_Rate number(1),constraint restaurant_pk Primary key (Restaurant_ID));create table Restaurants_Addresses(Restaurant_ID number(10),Address varchar2(15), constraint restAdd_pk Primary key (Restaurant_ID,Address),constraint restaurantid_fk foreign key (Restaurant_ID) references Restaurant(Restaurant_ID));create table Dishs(Restaurant_ID number(10),Dish_Name varchar2(20),Price number(4),constraint dishs_pk Primary key (Restaurant_ID,Dish_Name),constraint restaurantid_fk foreign key (Restaurant_ID) references Restaurant(Restaurant_ID));create table Order_Preparation(Order_ID number(10),Pharmacy_ID number(10),Restaurant_ID number(10),constraint OrderPrep_pk Primary key (Restaurant_ID,Order_ID,Pharmacy_ID),constraint restaurantid_fk foreign key (Restaurant_ID) references Restaurant(Restaurant_ID),constraint pharid_fk foreign key (Pharmacy_ID) references Pharmacy(Pharmacy_ID),constraint orderitems_fk foreign key (Order_ID) references Orders(Order_ID));